body{display:flex;min-height:100vh}.sidebar{position:fixed;right:0;top:0;height:100vh;width:280px;background:var(--dark-card);border-left:1px solid var(--dark-border);display:flex;flex-direction:column;z-index:1000;overflow-y:auto}.sidebar-header{padding:2rem;border-bottom:1px solid var(--dark-border)}.logo-icon-small{width:40px;height:40px;background:var(--gradient-neon);border-radius:12px;display:flex;align-items:center;justify-content:center;font-size:1.25rem;box-shadow:0 0 20px rgba(0,229,255,0.5)}.sidebar-logo{display:flex;align-items:center;gap:.75rem}.nav-link{display:flex;align-items:center;gap:1rem;padding:.875rem 1.5rem;color:#B0B0B0;text-decoration:none;border-radius:12px;transition:all .3s}.nav-link:hover{background:var(--dark-card-light);color:var(--neon-cyan)}.nav-item.active .nav-link{background:linear-gradient(135deg,#00E5FF,#7C4DFF);color:#fff}.badge{padding:.25rem .5rem;border-radius:12px;font-size:.75rem;font-weight:700;margin-left:auto}.badge-cyan{background:var(--neon-cyan);color:#0A0A0A}.main-wrapper{flex:1;margin-right:280px;min-height:100vh;display:flex;flex-direction:column}.header{height:70px;background:var(--dark-card);border-bottom:1px solid var(--dark-border);padding:0 2rem;display:flex;align-items:center;justify-content:space-between;position:sticky;top:0;z-index:900}.search-bar{display:flex;align-items:center;gap:1rem;background:var(--dark-card-light);padding:.75rem 1.5rem;border-radius:12px;border:1px solid var(--dark-border);max-width:500px;flex:1}.search-bar input{border:none;background:none;outline:none;font-family:inherit;color:#fff;width:100%}.user-menu-btn{display:flex;align-items:center;gap:1rem;background:var(--dark-card-light);border:1px solid var(--dark-border);padding:.5rem 1.5rem;border-radius:12px;cursor:pointer}.user-avatar{width:36px;height:36px;border-radius:50%;background:var(--gradient-neon);display:flex;align-items:center;justify-content:center}.user-dropdown{position:absolute;top:calc(100% + 8px);left:0;background:var(--dark-card);border:1px solid var(--dark-border);border-radius:12px;min-width:220px;opacity:0;visibility:hidden}.user-dropdown.active{opacity:1;visibility:visible}.dropdown-item{display:flex;align-items:center;gap:1rem;padding:.875rem 1.5rem;color:#B0B0B0;text-decoration:none}.dropdown-item:hover{background:var(--dark-card-light);color:var(--neon-cyan)}.main-content{flex:1;padding:2rem}.page-title{font-size:2rem;font-weight:800;margin-bottom:.5rem}.stats-grid{display:grid;grid-template-columns:repeat(auto-fit,minmax(250px,1fr));gap:1.5rem;margin-bottom:2rem}.stat-card{background:var(--dark-card);border-radius:16px;padding:2rem;border:1px solid var(--dark-border);display:flex;gap:1.5rem;transition:all .3s}.stat-card:hover{transform:translateY(-4px)}.stat-card.glow-cyan:hover{border-color:var(--neon-cyan);box-shadow:0 0 20px rgba(0,229,255,0.5)}.stat-icon{width:56px;height:56px;border-radius:12px;display:flex;align-items:center;justify-content:center;font-size:1.5rem}.stat-icon.cyan{background:rgba(0,229,255,.1);color:var(--neon-cyan)}.stat-icon.purple{background:rgba(124,77,255,.1);color:var(--neon-purple)}.stat-title{font-size:.875rem;color:#B0B0B0;margin-bottom:.5rem}.stat-value{font-size:2rem;font-weight:800;margin-bottom:.5rem}.stat-change{display:flex;align-items:center;gap:.25rem;font-size:.8125rem}.stat-change.positive{color:var(--success)}.stat-change.negative{color:#FF1744}.charts-row{display:grid;grid-template-columns:2fr 1fr;gap:1.5rem;margin-bottom:2rem}.chart-card{background:var(--dark-card);border-radius:16px;padding:2rem;border:1px solid var(--dark-border)}.chart-header{display:flex;align-items:center;justify-content:space-between;margin-bottom:1.5rem}.chart-title{font-size:1.125rem;font-weight:700}.chart-body{position:relative;height:300px}@media (max-width:1024px){.sidebar{transform:translateX(100%)}.sidebar.active{transform:translateX(0)}.main-wrapper{margin-right:0}.mobile-menu-btn{display:block}}@media (max-width:768px){.stats-grid{grid-template-columns:1fr}.search-bar{display:none}.charts-row{grid-template-columns:1fr}}
