# Create auth.js
cat > js/auth.js << 'AUTHJS'
const API_URL='https://mediaprosocial.io/api';function togglePassword(){const p=document.getElementById('password'),t=document.getElementById('toggleIcon');if(p&&t){if(p.type==='password'){p.type='text';t.classList.remove('fa-eye');t.classList.add('fa-eye-slash')}else{p.type='password';t.classList.remove('fa-eye-slash');t.classList.add('fa-eye')}}}function showError(m){const a=document.getElementById('errorAlert'),e=document.getElementById('errorMessage');if(a&&e){e.textContent=m;a.style.display='flex';setTimeout(()=>a.style.display='none',5000)}}function showSuccess(m){showError(m)}function showLoading(s=true){const l=document.getElementById('loadingOverlay');if(l)l.style.display=s?'flex':'none'}document.addEventListener('DOMContentLoaded',function(){const f=document.getElementById('loginForm');if(f){f.addEventListener('submit',async function(e){e.preventDefault();const email=document.getElementById('email')?.value;const password=document.getElementById('password')?.value;if(!email||!password){showError('الرجاء إدخال البريد وكلمة المرور');return}showLoading(true);try{const r=await fetch(`${API_URL}/login`,{method:'POST',headers:{'Content-Type':'application/json','Accept':'application/json'},body:JSON.stringify({email,password})});const d=await r.json();if(r.ok&&d.success){localStorage.setItem('auth_token',d.token);localStorage.setItem('user_data',JSON.stringify(d.user));showSuccess('تم تسجيل الدخول بنجاح');setTimeout(()=>window.location.href='dashboard.html',1000)}else{showError(d.message||'فشل تسجيل الدخول')}}catch(err){console.error(err);showError('حدث خطأ في الاتصال')}finally{showLoading(false)}})}});function logout(){localStorage.clear();window.location.href='login.html'}function getUserData(){const u=localStorage.getItem('user_data');return u?JSON.parse(u):null}function getAuthToken(){return localStorage.getItem('auth_token')}async function apiCall(endpoint,options={}){const token=getAuthToken();const headers={'Content-Type':'application/json','Accept':'application/json',...options.headers};if(token)headers['Authorization']=`Bearer ${token}`;try{const r=await fetch(`${API_URL}${endpoint}`,{...options,headers});if(r.status===401){logout();return}return await r.json()}catch(error){console.error(error);throw error}}window.authUtils={logout,getUserData,getAuthToken,apiCall,showError,showSuccess,showLoading};
AUTHJS

# Create dashboard.js
cat > js/dashboard.js << 'DASHJS'
document.addEventListener('DOMContentLoaded',function(){loadUserData();initEventListeners()});function loadUserData(){const u=window.authUtils?.getUserData();if(u){document.querySelectorAll('#userName,#userNameMain').forEach(el=>{if(el)el.textContent=u.name.split(' ')[0]})}}function initEventListeners(){const mBtn=document.getElementById('mobileMenuBtn'),sb=document.getElementById('sidebar');if(mBtn&&sb)mBtn.addEventListener('click',()=>sb.classList.toggle('active'));const uBtn=document.getElementById('userMenuBtn'),uDrop=document.getElementById('userDropdown');if(uBtn&&uDrop){uBtn.addEventListener('click',(e)=>{e.stopPropagation();uDrop.classList.toggle('active')});document.addEventListener('click',(e)=>{if(!uBtn.contains(e.target)&&!uDrop.contains(e.target))uDrop.classList.remove('active')})}}
DASHJS

