adow:var(--shadow-cyan),0 8px 24px rgba(0,0,0,0.3)}.btn-cyan{background:var(--neon-cyan);color:var(--dark-bg);box-shadow:var(--shadow-cyan)}.btn-cyan:hover{transform:translateY(-2px)}.btn-block{width:100%}.btn-large{padding:1.125rem 2rem;font-size:1.125rem}.form-group{display:flex;flex-direction:column;gap:var(--spacing-xs);margin-bottom:var(--spacing-md)}.form-group label{font-size:0.875rem;font-weight:600;color:var(--text-secondary)}.form-input{width:100%;padding:0.875rem 1rem;background:var(--dark-card);border:1px solid var(--dark-border);border-radius:var(--radius-md);color:var(--text-primary);font-size:1rem;font-family:inherit;transition:var(--transition)}.form-input:focus{outline:none;border-color:var(--neon-cyan);box-shadow:0 0 0 3px rgba(0,229,255,0.1)}.form-input::placeholder{color:var(--text-light)}.input-wrapper{position:relative;display:flex;align-items:center}.input-wrapper i{position:absolute;right:1rem;color:var(--text-light);font-size:1rem}.input-wrapper input{padding-right:3rem}.neon-orb{position:absolute;border-radius:50%;filter:blur(60px);opacity:0.3;animation:float 20s ease-in-out infinite}.orb-cyan{background:var(--neon-cyan);box-shadow:0 0 100px var(--neon-cyan)}.orb-purple{background:var(--neon-purple);box-shadow:0 0 100px var(--neon-purple)}.orb-magenta{background:var(--neon-magenta);box-shadow:0 0 100px var(--neon-magenta)}@keyframes float{0%,100%{transform:translate(0,0) scale(1)}33%{transform:translate(50px,-50px) scale(1.1)}66%{transform:translate(-30px,30px) scale(0.9)}}.spinner{width:48px;height:48px;border:4px solid rgba(255,255,255,0.1);border-top-color:var(--neon-cyan);border-radius:50%;animation:spin 0.8s linear infinite}@keyframes spin{to{transform:rotate(360deg)}}.loading-overlay{position:fixed;top:0;left:0;width:100%;height:100%;background:rgba(10,10,10,0.9);backdrop-filter:blur(10px);display:flex;flex-direction:column;align-items:center;justify-content:center;gap:var(--spacing-md);z-index:9999}.alert{padding:1rem;border-radius:var(--radius-md);display:flex;align-items:center;gap:var(--spacing-sm);font-size:0.875rem;animation:slideDown 0.3s ease-out;margin-bottom:var(--spacing-md)}@keyframes slideDown{from{opacity:0;transform:translateY(-10px)}to{opacity:1;transform:translateY(0)}}.alert-error{background:rgba(255,23,68,0.1);color:var(--error);border:1px solid var(--error)}.alert-success{background:rgba(0,230,118,0.1);color:var(--success);border:1px solid var(--success)}.text-center{text-align:center}.text-secondary{color:var(--text-secondary)}@media (max-width:768px){:root{--spacing-lg:1.5rem;--spacing-xl:2rem}.btn{font-size:0.9375rem;padding:0.75rem 1.25rem}}
